import React, { useState, useEffect } from "react";
import axios from "axios";
import "./styles/BookAppointment.css";
import { useNavigate, useLocation } from "react-router-dom";

function BookAppointment() {
  const [services, setServices] = useState([]);
  const [selectedServices, setSelectedServices] = useState([]);
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [customerName, setCustomerName] = useState("");
  const [otp, setOtp] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/services"); // Change to full URL
        setServices(response.data);
      } catch (error) {
        console.error("Error fetching services:", error.response ? error.response.data : error);
      }
    };

    fetchServices();

    if (location.state && location.state.selectedServices) {
      setSelectedServices(location.state.selectedServices);
    }
  }, [location.state]);


  const sendOtp = async () => {
    if (contactNumber.length !== 10) {
      alert("Enter a valid 10-digit contact number.");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/appointments/send-otp", { contactNumber });
      alert("OTP sent successfully.");
      setOtpSent(true);
    } catch (error) {
      console.error("Error sending OTP:", error.response ? error.response.data : error);
    }
  };

  const verifyOtp = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/appointments/verify-otp", {
        contactNumber,
        otp,
      });

      if (response.data.success) {
        alert("OTP verified successfully.");
        setOtpVerified(true);
      } else {
        alert("Invalid OTP, please try again.");
      }
    } catch (error) {
      console.error("Error verifying OTP:", error.response ? error.response.data : error);
    }
  };

  const handleAppointmentBooking = async (e) => {
    e.preventDefault();
  
    // Validate required fields
    if (!customerName || !contactNumber || selectedServices.length === 0 || !appointmentDate || !appointmentTime) {
      alert("All fields are required.");
      return;
    }
  
    // Ensure OTP is verified before booking
    if (!otpVerified) {
      alert("Please verify OTP first.");
      return;
    }
  
    try {
      const response = await axios.post("http://localhost:5000/api/appointments/add", {
        customerName,
        contactNumber,
        services: selectedServices.map((s) => s._id), // Ensure _id exists in service objects
        appointmentDate,
        appointmentTime,
        otp, // ✅ Include OTP
      });
  
      if (response.status === 201) {
        alert("Success! We’ll call you later to confirm the appointment.");
        navigate("/"); // Redirect after successful booking
      } else {
        alert(response.data.message || "Failed to book appointment.");
      }
    } catch (error) {
      console.error("Error booking appointment:", error);
      alert(error.response?.data?.message || "Server error. Try again later.");
    }
  };
  

  return (
    <div className="booking-container">
      <div style={{ marginTop: "90px" }}></div>
      <div className="banner">
        <div className="banner-overlay">
          <h1>Book Your Beauty Session</h1>
          <p>Your Perfect Look Awaits</p>
        </div>
      </div>

      <div className="container form-wrapper">
        <div className="booking-card">
          <h2 className="form-title">Appointment Details</h2>
          <form onSubmit={handleAppointmentBooking}>
            <div className="row g-3">
              <div className="col-md-6">
                <div className="form-group">
                  <label>Full Name</label>
                  <input
                    type="text"
                    className="form-control"
                    value={customerName}
                    onChange={(e) => setCustomerName(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-group">
                  <label>Contact Number</label>
                  <div className="d-flex">
                    <input
                      type="text"
                      className="form-control me-2"
                      value={contactNumber}
                      onChange={(e) => {
                        const value = e.target.value;
                        if (/^\d{0,10}$/.test(value)) setContactNumber(value);
                      }}
                      required
                    />
                    <button type="button" className="btn btn-secondary" onClick={sendOtp} disabled={otpSent}>
                      {otpSent ? "OTP Sent" : "Send OTP"}
                    </button>
                  </div>
                </div>
              </div>
            </div>

            {otpSent && (
              <div className="form-group mt-3">
                <label>Enter OTP</label>
                <div className="d-flex">
                  <input
                    type="text"
                    className="form-control me-2"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    required
                  />
                  <button type="button" className="btn btn-success" onClick={verifyOtp}>
                    Verify OTP
                  </button>
                </div>
              </div>
            )}

            <div className="form-group">
              <label>Selected Services</label>
              <ul className="list-group">
                {selectedServices.length > 0 ? (
                  selectedServices.map((service) => (
                    <li key={service._id} className="list-group-item">
                      {service.name}
                    </li>
                  ))
                ) : (
                  <li className="list-group-item text-muted">No services selected</li>
                )}
              </ul>
            </div>

            <div className="row g-3">
              <div className="col-md-6">
                <div className="form-group">
                  <label>Date</label>
                  <input
                    type="date"
                    className="form-control"
                    value={appointmentDate}
                    min={new Date().toISOString().split("T")[0]}
                    onChange={(e) => setAppointmentDate(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-group">
                  <label>Time</label>
                  <input
                    type="time"
                    className="form-control"
                    value={appointmentTime}
                    onChange={(e) => setAppointmentTime(e.target.value)}
                    required
                  />
                </div>
              </div>
            </div>

            <button
              type="submit"
              className="btn submit-btn mt-3"
              disabled={!otpVerified}
              onClick={handleAppointmentBooking}  // ✅ Add this function call
            >
              Confirm Booking
            </button>

          </form>
        </div>
      </div>
    </div>
  );
}

export default BookAppointment;
