import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServicesPage = () => {
  const [services, setServices] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedService, setSelectedService] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/services');
      setServices(response.data);
    } catch (err) {
      console.error('Error fetching services:', err);
      setError('Failed to load services. Please try again later.');
    }
  };

  const handleServiceClick = async (serviceId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/services/${serviceId}/categories`);
      setSelectedService(serviceId);
      setCategories(response.data);
    } catch (err) {
      console.error('Error fetching categories:', err);
      setError('Failed to load categories. Please try again later.');
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Our Services</h1>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {services.length > 0 ? (
          services.map((service) => (
            <div
              key={service._id}
              className="bg-white shadow-md rounded-lg p-4 cursor-pointer hover:shadow-lg"
              onClick={() => handleServiceClick(service._id)}
            >
              <h2 className="text-xl font-semibold mb-2">{service.name}</h2>
              <p className="text-gray-600">{service.description}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No services available. Please check back later.</p>
        )}
      </div>

      {categories.length > 0 && (
        <div className="mt-8">
          <h2 className="text-2xl font-bold mb-4">Categories for Selected Service</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {categories.map((category) => (
              <div key={category._id} className="bg-gray-100 p-4 rounded-md shadow-sm">
                <h4 className="text-lg font-medium">{category.treatmentName}</h4>
                <p className="text-gray-600">{category.description}</p>
                <p className="text-gray-700 font-bold">Price: ${category.price}</p>
                <p className="text-gray-600">Estimated Time: {category.estimatedTime} mins</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ServicesPage;
