import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServiceForm = () => {
  const [categories, setCategories] = useState([]); // For storing category list
  const [services, setServices] = useState([]); // For storing services
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    categoryId: '',
    price: '',
    estimatedTime: '',
  });

  // Fetch categories on component mount
  useEffect(() => {
    axios
      .get('/api/admin/categories')
      .then((res) => setCategories(res.data))
      .catch((err) => console.error('Error fetching categories:', err));
  }, []);

  // Fetch all services on component mount
  useEffect(() => {
    axios
      .get('/api/admin/services')
      .then((res) => setServices(res.data))
      .catch((err) => console.error('Error fetching services:', err));
  }, []);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('/api/admin/services', formData)
      .then((res) => {
        alert('Service added successfully!');
        setFormData({ name: '', description: '', categoryId: '', price: '', estimatedTime: '' });
        setServices((prevServices) => [...prevServices, res.data]); // Add new service to state
      })
      .catch((err) => console.error('Error adding service:', err));
  };

  // Handle service deletion
  const handleDelete = (serviceId) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      axios
        .delete(`/api/admin/services/${serviceId}`)
        .then(() => {
          alert('Service deleted successfully!');
          setServices((prevServices) => prevServices.filter((service) => service._id !== serviceId));
        })
        .catch((err) => console.error('Error deleting service:', err));
    }
  };

  return (
    <div className="container mt-4">
      <h2>Manage Services</h2>

      {/* Form to add a new service */}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Service Name</label>
          <input
            type="text"
            className="form-control"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Description</label>
          <textarea
            className="form-control"
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            required
          ></textarea>
        </div>
        <div className="mb-3">
          <label className="form-label">Price</label>
          <input
            type="number"
            className="form-control"
            value={formData.price}
            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Estimated Time (minutes)</label>
          <input
            type="number"
            className="form-control"
            value={formData.estimatedTime}
            onChange={(e) => setFormData({ ...formData, estimatedTime: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Category</label>
          <select
            className="form-select"
            value={formData.categoryId}
            onChange={(e) => setFormData({ ...formData, categoryId: e.target.value })}
            required
          >
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category._id} value={category._id}>
                {category.treatmentName}
              </option>
            ))}
          </select>
        </div>
        <button type="submit" className="btn btn-primary">Add Service</button>
      </form>

      <hr />

      {/* Display services */}
      <h3>Services</h3>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Service Name</th>
            <th>Description</th>
            <th>Category</th>
            <th>Price</th>
            <th>Estimated Time</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {services.map((service) => (
            <tr key={service._id}>
              <td>{service.name}</td>
              <td>{service.description}</td>
              <td>
                {
                  categories.find((category) => category._id === service.categoryId)
                    ?.treatmentName || 'Unknown'
                }
              </td>
              <td>{service.price}</td>
              <td>{service.estimatedTime} minutes</td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDelete(service._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ServiceForm;
