import React from "react";
import Masonry from "react-masonry-css";
import "bootstrap/dist/css/bootstrap.min.css";
import "./styles/Gallery.css"; // Import custom CSS for styling

const images = Array.from({ length: 85 }, (_, index) => ({
  id: index + 1,
  src: `https://source.unsplash.com/random/300x${200 + (index % 50)}`, // Generates random images
  alt: `Gallery Image ${index + 1}`,
}));

const Gallery = () => {
  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Our Work</h2>

      {/* Masonry Grid */}
      <Masonry
        breakpointCols={{ default: 4, 1024: 3, 768: 2, 576: 1 }} // Responsive breakpoints
        className="masonry-grid"
        columnClassName="masonry-column"
      >
        {images.map((image) => (
          <div key={image.id} className="masonry-item">
            <img
              src={image.src}
              alt={image.alt}
              className="gallery-image"
              loading="lazy"
            />
          </div>
        ))}
      </Masonry>
    </div>
  );
};

export default Gallery;
