import React, { useState } from 'react';
import { Menu, X, Home, Info, Phone, Star } from 'react-feather'; // Added icons for navigation

function Navbar() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setIsMenuOpen(false);
    }
  };

  return (
    <nav className="fixed w-full bg-gradient-to-r from-violet-500 via-purple-600 to-pink-500 text-white shadow-lg z-50">
      <div className="max-w-6xl mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          {/* Logo Section */}
          <div className="flex items-center gap-2">
            <Star className="w-6 h-6 text-yellow-400" />
            <span className="font-bold text-lg">Bella Beauty</span>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center gap-8">
            <button
              onClick={() => scrollToSection('home')}
              className="flex items-center gap-1 hover:text-yellow-400 transition-colors"
            >
              <Home className="w-4 h-4" /> Home
            </button>
            <button
              onClick={() => scrollToSection('about')}
              className="flex items-center gap-1 hover:text-yellow-400 transition-colors"
            >
              <Info className="w-4 h-4" /> About
            </button>
            <button
              onClick={() => scrollToSection('services')}
              className="flex items-center gap-1 hover:text-yellow-400 transition-colors"
            >
              <Info className="w-4 h-4" /> Services
            </button>
            <button
              onClick={() => scrollToSection('contact')}
              className="flex items-center gap-1 hover:text-yellow-400 transition-colors"
            >
              <Phone className="w-4 h-4" /> Contact
            </button>
            <button
              onClick={() => scrollToSection('book')}
              className="bg-yellow-400 hover:bg-yellow-500 text-black px-4 py-2 rounded-full text-sm transition-all"
            >
              Book Now
            </button>
          </div>

          {/* Mobile Menu Button */}
          <button
            className="md:hidden text-white"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            {isMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isMenuOpen && (
        <div className="md:hidden bg-white text-gray-800 border-t border-gray-300">
          <div className="px-4 py-2 space-y-2">
            <button
              onClick={() => scrollToSection('home')}
              className="block w-full text-left px-3 py-2 hover:bg-violet-100 rounded-md"
            >
              Home
            </button>
            <button
              onClick={() => scrollToSection('about')}
              className="block w-full text-left px-3 py-2 hover:bg-violet-100 rounded-md"
            >
              About
            </button>
            <button
              onClick={() => scrollToSection('services')}
              className="block w-full text-left px-3 py-2 hover:bg-violet-100 rounded-md"
            >
              Services
            </button>
            <button
              onClick={() => scrollToSection('contact')}
              className="block w-full text-left px-3 py-2 hover:bg-violet-100 rounded-md"
            >
              Contact
            </button>
            <button
              onClick={() => scrollToSection('book')}
              className="block w-full text-left px-3 py-2 bg-yellow-400 text-black hover:bg-yellow-500 rounded-md font-semibold"
            >
              Book Now
            </button>
          </div>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
