import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const StaffAttendanceCard = ({ apiPath = "http://localhost:5000/api/attendance", title, description, color }) => {
  const [view, setView] = useState("default");
  const [staffList, setStaffList] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [newStaff, setNewStaff] = useState({
    staffId: "",
    staffName: "",
    email: "",
    contact: "",
  });
  const [attendanceRecord, setAttendanceRecord] = useState({});
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split("T")[0]);

  const fetchStaff = async () => {
    try {
      const response = await axios.get(`${apiPath}/`);
      const uniqueStaff = Array.from(new Map(response.data.map(staff => [staff.staffId, staff])).values());
      setStaffList(uniqueStaff);
    } catch (error) {
      console.error("Error fetching staff:", error);
    }
  };

  const fetchAttendance = async (date) => {
    try {
      const response = await axios.get(`${apiPath}/?date=${date}`);
      setAttendance(response.data.length > 0 ? response.data : []);
    } catch (error) {
      console.error("Error fetching attendance:", error);
    }
  };

  const addStaff = async () => {
    const { staffId, staffName } = newStaff;
    if (!staffId || !staffName) {
      alert("All fields are required");
      return;
    }
    try {
      const response = await axios.post(`${apiPath}/staff`, newStaff);
      if (response.status === 201) {
        alert("Staff added successfully");
        setNewStaff({ staffId: "", staffName: "" });
        fetchStaff();
      } else {
        alert(response.data.message || "Error adding staff");
      }
    } catch (error) {
      console.error("Error adding staff:", error);
      alert("Error adding staff. Please try again.");
    }
  };

  const saveAttendance = async () => {
    try {
      const formattedAttendance = Object.entries(attendanceRecord).map(([staffId, status]) => {
        const staff = staffList.find(staff => staff.staffId === staffId);
        return { staffId, staffName: staff.staffName, status, date: selectedDate };
      });

      if (formattedAttendance.length > 0) {
        await axios.post(`${apiPath}/attendance/mark`, formattedAttendance);
        alert("Attendance saved successfully!");
        setAttendanceRecord({});
        fetchAttendance(selectedDate);
      } else {
        alert("No valid attendance records to save.");
      }
    } catch (error) {
      console.error("Error saving attendance:", error);
      alert("Already Marked for Today.");
    }
  };

  useEffect(() => {
    if (view === "mark") fetchStaff();
    if (view === "fetch") fetchAttendance(selectedDate);
  }, [view, selectedDate]);

  return (
    <div className={`card border-${color} mb-4 shadow-lg`}>
      <div className={`card-header bg-${color} text-white fw-bold text-center`}>{title}</div>
      <div className="card-body">
        <p className="text-muted text-center">{description}</p>
        <div className="d-flex justify-content-center gap-3 mb-3">
          <button className="btn btn-outline-primary" onClick={() => setView("add")}>Add Staff</button>
          <button className="btn btn-outline-secondary" onClick={() => setView("mark")}>Mark Attendance</button>
          <button className="btn btn-outline-success" onClick={() => setView("fetch")}>Fetch Data</button>
        </div>

        {view === "add" && (
          <div>
            <h5 className="text-center">Add Staff</h5>
            <input type="text" placeholder="Staff ID" className="form-control mb-2" value={newStaff.staffId} onChange={(e) => setNewStaff({ ...newStaff, staffId: e.target.value })} />
            <input type="text" placeholder="Staff Name" className="form-control mb-2" value={newStaff.staffName} onChange={(e) => setNewStaff({ ...newStaff, staffName: e.target.value })} />
            <button className="btn btn-success w-100" onClick={addStaff}>Add Staff</button>
          </div>
        )}

        {view === "mark" && (
          <div>
            <h5 className="text-center">Mark Attendance</h5>
            {staffList.map((staff, index) => (
              <div key={staff.staffId || index} className="d-flex align-items-center justify-content-between border p-2 rounded mb-2">
                <span>{staff.staffName}</span>
                <div>
                  <label className="me-3">
                    <input type="radio" name={`attendance-${staff.staffId}`} value="Present" checked={attendanceRecord[staff.staffId] === "Present"} onChange={() => setAttendanceRecord({ ...attendanceRecord, [staff.staffId]: "Present" })} /> Present
                  </label>
                  <label>
                    <input type="radio" name={`attendance-${staff.staffId}`} value="Absent" checked={attendanceRecord[staff.staffId] === "Absent"} onChange={() => setAttendanceRecord({ ...attendanceRecord, [staff.staffId]: "Absent" })} /> Absent
                  </label>
                </div>
              </div>
            ))}
            <button className="btn btn-warning w-100 mt-3" onClick={saveAttendance}>Save Attendance</button>
          </div>
        )}

        {view === "fetch" && (
          <div>
            <h5 className="text-center">Attendance Records</h5>
            <input type="date" className="form-control mb-3" value={selectedDate} onChange={(e) => setSelectedDate(e.target.value)} />
            <table className="table table-bordered table-striped">
              <thead className="table-dark">
                <tr>
                  <th>Name</th>
                  <th>Date</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {attendance.map((record) => (
                  <tr key={record._id}>
                    <td>{record.staffName}</td>
                    <td>{new Date(record.date).toLocaleDateString()}</td>
                    <td>{record.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default StaffAttendanceCard;
