import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServicesPage = () => {
  const [services, setServices] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/services');
      setServices(response.data);
    } catch (err) {
      console.error('Error fetching services:', err);
      setError('Failed to load services. Please try again later.');
    }
  };

  const handleCategoryClick = async (categoryId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/categories/${categoryId}/services`);
      setSelectedCategory(response.data);  // Assuming response contains services for the category
      setModalVisible(true);  // Open the modal
    } catch (err) {
      console.error('Error fetching category services:', err);
      setError('Failed to load services for this category. Please try again later.');
    }
  };

  const closeModal = () => {
    setModalVisible(false);  // Close the modal
    setSelectedCategory(null);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Our Services</h1>
      {error && <p className="text-red-500 mb-4">{error}</p>}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {categories.length > 0 ? (
          categories.map((category) => (
            <div
              key={category._id}
              className="bg-white shadow-md rounded-lg p-4 cursor-pointer hover:shadow-lg"
              onClick={() => handleCategoryClick(category._id)}
            >
              <h2 className="text-xl font-semibold mb-2">{category.name}</h2>
              <p className="text-gray-600">{category.description}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No categories available. Please check back later.</p>
        )}
      </div>

      {modalVisible && selectedCategory && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg w-96">
            <h2 className="text-2xl font-bold mb-4">Services for {selectedCategory.name}</h2>
            <div className="grid grid-cols-1 gap-4">
              {selectedCategory.services && selectedCategory.services.length > 0 ? (
                selectedCategory.services.map((service) => (
                  <div key={service._id} className="p-4 bg-gray-100 rounded-md shadow-sm">
                    <h4 className="text-lg font-medium">{service.name}</h4>
                    <p className="text-gray-600">{service.description}</p>
                    <p className="text-gray-700 font-bold">Price: ${service.price}</p>
                    <p className="text-gray-600">Estimated Time: {service.estimatedTime} mins</p>
                  </div>
                ))
              ) : (
                <p className="text-gray-500">No services available for this category.</p>
              )}
            </div>
            <button
              onClick={closeModal}
              className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ServicesPage;
