import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

const BookAppointment = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedServices, setSelectedServices] = useState([]);
  const [appointmentDetails, setAppointmentDetails] = useState({
    name: "",
    phone: "",
    date: "",
    time: ""
  });

  useEffect(() => {
    if (location.state && location.state.selectedServices) {
      setSelectedServices(location.state.selectedServices);
    }
  }, [location.state]);

  const handleChange = (e) => {
    setAppointmentDetails({ ...appointmentDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/api/appointments/book", {
        ...appointmentDetails,
        services: selectedServices,
      });
      alert("Appointment booked successfully!");
      navigate("/");
    } catch (error) {
      console.error("Error booking appointment", error);
      alert("Failed to book appointment. Please try again.");
    }
  };

  return (
    <div className="container mt-5">
      <h2>Confirm Your Appointment</h2>
      {selectedServices.length > 0 ? (
        <div>
          <h4>Selected Services:</h4>
          <ul>
            {selectedServices.map((service, index) => (
              <li key={index}>{service.name} - ${service.price} x {service.quantity}</li>
            ))}
          </ul>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label>Name:</label>
              <input type="text" name="name" className="form-control" value={appointmentDetails.name} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label>Phone:</label>
              <input type="text" name="phone" className="form-control" value={appointmentDetails.phone} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label>Date:</label>
              <input type="date" name="date" className="form-control" value={appointmentDetails.date} onChange={handleChange} required />
            </div>
            <div className="mb-3">
              <label>Time:</label>
              <input type="time" name="time" className="form-control" value={appointmentDetails.time} onChange={handleChange} required />
            </div>
            <button type="submit" className="btn btn-primary">Confirm Appointment</button>
          </form>
        </div>
      ) : (
        <p>No services selected. Please add services to your cart.</p>
      )}
    </div>
  );
};

export default BookAppointment;
