import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [appointments, setAppointments] = useState([]);
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState("");
  const [staffAttendance, setStaffAttendance] = useState([]);

  useEffect(() => {
    fetchAppointments();
    fetchServices();
    fetchStaffAttendance();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await axios.get("/api/appointments/today");
      setAppointments(response.data);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  const fetchServices = async () => {
    try {
      const response = await axios.get("/api/services");
      setServices(response.data);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  const fetchStaffAttendance = async () => {
    try {
      const response = await axios.get("/api/staff/attendance");
      setStaffAttendance(response.data);
    } catch (error) {
      console.error("Error fetching staff attendance:", error);
    }
  };

  const addService = async () => {
    if (!newService) return alert("Service name cannot be empty!");
    try {
      const response = await axios.post("/api/services", { name: newService });
      setServices([...services, response.data]);
      setNewService("");
    } catch (error) {
      console.error("Error adding service:", error);
    }
  };

  const deleteService = async (id) => {
    try {
      await axios.delete(`/api/services/${id}`);
      setServices(services.filter((service) => service._id !== id));
    } catch (error) {
      console.error("Error deleting service:", error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Admin Dashboard</h1>
      <div className="row">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header bg-primary text-white">
              <h4>Today's Appointments</h4>
            </div>
            <div className="card-body">
              {appointments.length > 0 ? (
                <ul className="list-group">
                  {appointments.map((appointment) => (
                    <li key={appointment._id} className="list-group-item">
                      {appointment.time} - {appointment.clientName}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No appointments for today.</p>
              )}
            </div>
          </div>
        </div>

        <div className="col-md-6">
          <div className="card">
            <div className="card-header bg-secondary text-white">
              <h4>Manage Staff Attendance</h4>
            </div>
            <div className="card-body">
              {staffAttendance.length > 0 ? (
                <ul className="list-group">
                  {staffAttendance.map((staff) => (
                    <li key={staff._id} className="list-group-item">
                      {staff.name} - {staff.status ? "Present" : "Absent"}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No staff attendance recorded yet.</p>
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-md-12">
          <div className="card">
            <div className="card-header bg-success text-white">
              <h4>Manage Services</h4>
            </div>
            <div className="card-body">
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="New Service Name"
                  value={newService}
                  onChange={(e) => setNewService(e.target.value)}
                />
                <button className="btn btn-success mt-2" onClick={addService}>
                  Add Service
                </button>
              </div>
              {services.length > 0 ? (
                <ul className="list-group">
                  {services.map((service) => (
                    <li key={service._id} className="list-group-item d-flex justify-content-between">
                      {service.name}
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => deleteService(service._id)}
                      >
                        Delete
                      </button>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No services available.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
