import React, { useState, useEffect } from "react";
import axios from "axios";

const StaffAttendanceCard = ({ apiPath = "http://localhost:5000/api/attendance", title, description, color }) => {
  const [view, setView] = useState("default"); // "add", "mark", "fetch"
  const [staffList, setStaffList] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [newStaff, setNewStaff] = useState({
    staffId: "",
    staffName: "",
    email: "",
    contact: "",
  });
  const [attendanceRecord, setAttendanceRecord] = useState({});
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split("T")[0]); // Default to today's date

  // Fetch all staff
  const fetchStaff = async () => {
    try {
      const response = await axios.get(`${apiPath}/`);
      setStaffList(response.data);
    } catch (error) {
      console.error("Error fetching staff:", error);
    }
  };

  // Fetch attendance data by date
  const fetchAttendance = async (date) => {
    try {
      const response = await axios.get(`${apiPath}?date=${date}`);
      setAttendance(response.data);
    } catch (error) {
      console.error("Error fetching attendance:", error);
    }
  };

  // Add new staff
  const addStaff = async () => {
    const { staffId, staffName} = newStaff;

    if (!staffId || !staffName) {
      alert("All fields are required");
      return;
    }

    try {
      const response = await axios.post(`${apiPath}/staff`, newStaff);  // Correct endpoint: /staff
      if (response.status === 201) {
        alert("Staff added successfully");
        setNewStaff({ staffId: "", staffName: "", email: "", contact: "" }); // Clear inputs
        fetchStaff(); // Fetch updated staff list
      } else {
        alert(response.data.message || "Error adding staff");
      }
    } catch (error) {
      console.error("Error adding staff:", error);
      alert("Error adding staff. Please try again.");
    }
  };

  // Save attendance
  const saveAttendance = async () => {
    try {
      // Format the attendance data
      const formattedAttendance = Object.entries(attendanceRecord).map(([staffId, status]) => {
        const staff = staffList.find(staff => staff.staffId === staffId); // Ensure staffId is used here
        if (!staff) {
          return null; // Skip if staff not found
        }
  
        return {
          staffId,
          staffName: staff.staffName, // Ensure staffName is passed
          status,
          date: selectedDate,
        };
      }).filter(Boolean); // Filter out any null values
  
      if (formattedAttendance.length > 0) {
        await axios.post(`${apiPath}/attendance/mark`, formattedAttendance);
        alert("Attendance saved successfully!");
        setAttendanceRecord({}); // Reset attendance record
      } else {
        alert("No valid attendance records to save.");
      }
    } catch (error) {
      console.error("Error saving attendance:", error);
      alert("Error saving attendance. Please try again.");
    }
  };
  

  // Handle view changes and fetch data accordingly
  useEffect(() => {
    if (view === "mark") fetchStaff();
    if (view === "fetch") fetchAttendance(selectedDate);
  }, [view, selectedDate]);

  return (
    <div className={`card border-${color} mb-4 shadow`}>
      <div style={{ marginTop: '70px' }}></div>

      <div className={`card-header bg-${color} text-white`}>{title}</div>
      <div className="card-body">
        <p>{description}</p>
        <div className="mb-3 d-flex justify-content-between">
          <button className="btn btn-primary" onClick={() => setView("add")}>
            Add Staff
          </button>
          <button className="btn btn-secondary" onClick={() => setView("mark")}>
            Mark Attendance
          </button>
          <button className="btn btn-success" onClick={() => setView("fetch")}>
            Fetch Data
          </button>
        </div>

        {/* Add Staff View */}
        {view === "add" && (
          <div>
            <h5>Add Staff</h5>
            <input
              type="text"
              placeholder="Enter staff ID"
              className="form-control mb-2"
              value={newStaff.staffId}
              onChange={(e) => setNewStaff({ ...newStaff, staffId: e.target.value })}
            />
            <input
              type="text"
              placeholder="Enter staff name"
              className="form-control mb-2"
              value={newStaff.staffName}
              onChange={(e) => setNewStaff({ ...newStaff, staffName: e.target.value })}
            />
            <button className="btn btn-success" onClick={addStaff}>
              Add Staff
            </button>
          </div>
        )}

        {/* Mark Attendance View */}
        {view === "mark" && (
          <div>
            <h5>Mark Attendance</h5>
            <form>
              {staffList.map((staff) => (
                <div key={staff._id} className="d-flex align-items-center mb-2">
                  <span className="me-3">{staff.staffName}</span>
                  <div>
                    <label className="me-2">
                      <input
                        type="radio"
                        name={`attendance-${staff._id}`}
                        value="Present"
                        onChange={() =>
                          setAttendanceRecord((prev) => ({ ...prev, [staff._id]: "Present" }))}
                      />{" "}
                      Present
                    </label>
                    <label>
                      <input
                        type="radio"
                        name={`attendance-${staff._id}`}
                        value="Absent"
                        onChange={() =>
                          setAttendanceRecord((prev) => ({ ...prev, [staff._id]: "Absent" }))}
                      />{" "}
                      Absent
                    </label>
                  </div>
                </div>
              ))}
              <button
                type="button"
                className="btn btn-warning mt-3"
                onClick={saveAttendance}
              >
                Save Attendance
              </button>
            </form>
          </div>
        )}

        {/* Fetch Data View */}
        {view === "fetch" && (
          <div>
            <h5>Fetch Attendance Records</h5>
            <input
              type="date"
              className="form-control mb-3"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            />
            <table className="table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Date</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {attendance.map((record) => (
                  <tr key={record._id}>
                    <td>{record.staffName}</td>
                    <td>{new Date(record.date).toLocaleDateString()}</td>
                    <td>{record.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default StaffAttendanceCard;
