import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/BookAppointment.css";


function BookAppointment() {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState("");
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [customerName, setCustomerName] = useState("");

  useEffect(() => {
    const fetchServices = async () => {
      const response = await axios.get("http://localhost:5000/api/services");
      setServices(response.data);
    };

    fetchServices();
  }, []);

  const handleAppointmentBooking = async (e) => {
    e.preventDefault();

    // Ensure all required fields are provided
    if (!customerName || !contactNumber || !selectedService || !appointmentDate || !appointmentTime) {
      alert("Please fill all fields.");
      return;
    }

    try {
      // Log the data being sent for debugging
    console.log({
      customerName,
      contactNumber,
      service: selectedService,
      appointmentDate,
      appointmentTime,
    });
      // Send the data to the backend API
      const response = await axios.post("http://localhost:5000/api/appointments/add", {
        customerName,
        contactNumber,
        service: selectedService,
        appointmentDate,
        appointmentTime,
      });
      alert("We will call you later to confirm the appointment.");
    } catch (error) {
      console.error("Error booking appointment:", error.response ? error.response.data : error);
    }
  };

  return (
    <div className="booking-container">
      {/* Banner Section */}
      <div className="banner">
        <div className="banner-overlay">
          <h1>Book Your Beauty Session</h1>
          <p>Your Perfect Look Awaits</p>
        </div>
      </div>

      {/* Form Section */}
      <div className="container form-wrapper">
        <div className="booking-card">
          <h2 className="form-title">
            <i className="fas fa-calendar-check me-2"></i>
            Appointment Details
          </h2>
          
          <form onSubmit={handleAppointmentBooking}>
            <div className="row g-3">
              <div className="col-md-6">
                <div className="form-group">
                  <label className="form-label">
                    <i className="fas fa-user icon"></i> Full Name
                  </label>
                  <input
                    type="text"
                    className="form-control input-field"
                    value={customerName}
                    onChange={(e) => setCustomerName(e.target.value)}
                    required
                  />
                </div>
              </div>

              <div className="col-md-6">
                <div className="form-group">
                  <label className="form-label">
                    <i className="fas fa-phone icon"></i> Contact Number
                  </label>
                  <input
                    type="text"
                    className="form-control input-field"
                    value={contactNumber}
                    onChange={(e) => setContactNumber(e.target.value)}
                    required
                  />
                </div>
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">
                <i className="fas fa-scissors icon"></i> Select Service
              </label>
              <select
                className="form-select input-field"
                value={selectedService}
                onChange={(e) => setSelectedService(e.target.value)}
                required
              >
                <option value="">-- Select Service --</option>
                {services.map((service) => (
                  <option key={service._id} value={service._id}>
                    {service.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="row g-3">
              <div className="col-md-6">
                <div className="form-group">
                  <label className="form-label">
                    <i className="fas fa-calendar-day icon"></i> Date
                  </label>
                  <div className="input-group">
                    <input
                      type="date"
                      className="form-control input-field date-picker"
                      value={appointmentDate}
                      onChange={(e) => setAppointmentDate(e.target.value)}
                      required
                    />
                    <span className="input-group-text">
                      <i className="fas fa-calendar-alt"></i>
                    </span>
                  </div>
                </div>
              </div>

              <div className="col-md-6">
                <div className="form-group">
                  <label className="form-label">
                    <i className="fas fa-clock icon"></i> Time
                  </label>
                  <div className="input-group">
                    <input
                      type="time"
                      className="form-control input-field time-picker"
                      value={appointmentTime}
                      onChange={(e) => setAppointmentTime(e.target.value)}
                      required
                    />
                    <span className="input-group-text">
                      <i className="fas fa-hourglass-start"></i>
                    </span>
                  </div>
                </div>
              </div>
            </div>

            <button type="submit" className="btn submit-btn">
              <i className="fas fa-bookmark me-2"></i>
              Confirm Booking
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
export default BookAppointment;
