import React, { useState, useEffect } from "react";
import axios from "axios";
import DashboardCard from "../components/DashboardCard";

const AdminDashboard = () => {
  const [appointments, setAppointments] = useState([]);
  const [staffAttendance, setStaffAttendance] = useState([]);
  const [services, setServices] = useState([]);

  const fetchAppointments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/appointments/today");
      setAppointments(response.data);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  const fetchStaffAttendance = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/staff/attendance");
      setStaffAttendance(response.data);
    } catch (error) {
      console.error("Error fetching staff attendance:", error);
    }
  };

  useEffect(() => {
    fetchAppointments();
    fetchStaffAttendance();
  }, []);

  const sections = [
    {
      title: "Today's Appointments",
      description: "View and manage today's appointments.",
      color: "primary",
      path: "/AppointmentsCard",
      path: "/AppointmentsCard",
    },
    {
      title: "Manage Staff Attendance",
      description: "View and update staff attendance records.",
      color: "secondary",
      path: "/staff-attendance",
    },
    {
      title: "Manage Services",
      description: "Add, update, or delete services offered.",
      color: "success",
      path: "/manage-services",
    },
  ];

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Admin Dashboard</h1>
      <div className="row">
        {sections.map((section, index) => (
          <div key={index} className="col-md-4">
            <DashboardCard
              title={section.title}
              description={section.description}
              color={section.color}
              onClick={() => window.location.assign(section.path)}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
