import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Services() {
  const [categories, setCategories] = useState([]); // Categories like Hair, Skin, etc.
  const [selectedCategory, setSelectedCategory] = useState(null); // Selected category details
  const [categoryDetails, setCategoryDetails] = useState([]); // Details of the selected category

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/categories'); // Fetch categories
        setCategories(response.data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  const fetchCategoryDetails = async (categoryId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/categories/${categoryId}/details`);
      setCategoryDetails(response.data); // Fetch treatments for the selected category
    } catch (error) {
      console.error('Error fetching category details:', error);
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    fetchCategoryDetails(category._id); // Fetch details when a category is clicked
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Our Services</h2>

      {/* Display Categories */}
      {!selectedCategory && (
        <div className="row">
          {categories.map((category) => (
            <div className="col-md-6 mb-4" key={category._id}>
              <div
                className="card h-100 shadow-lg"
                onClick={() => handleCategoryClick(category)}
                style={{ cursor: 'pointer' }}
              >
                <div className="card-body d-flex flex-column justify-content-center align-items-center">
                  <h3 className="card-title text-center">{category.name}</h3>
                  <p className="card-text text-center">{category.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Display Selected Category Details */}
      {selectedCategory && (
        <div>
          <button
            className="btn btn-secondary mb-3"
            onClick={() => setSelectedCategory(null)}
          >
            Back to Services
          </button>
          <h3 className="text-center">{selectedCategory.name} Treatments</h3>
          <div className="row">
            {categoryDetails.map((detail) => (
              <div className="col-md-6 mb-4" key={detail._id}>
                <div className="card shadow-sm">
                  <div className="card-body">
                    <h5 className="card-title">{detail.treatmentName}</h5>
                    <p className="card-text">{detail.description}</p>
                    <p className="card-text">Price: ${detail.price}</p>
                    <p className="card-text">Estimated Time: {detail.estimatedTime} mins</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default Services;
