import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaBars, FaTimes } from 'react-icons/fa';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <nav className="bg-white shadow-lg fixed w-full z-50">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between items-center h-20">
          <Link to="/" className="text-2xl font-bold text-pink-600">
            Salon Beauty
          </Link>

          <div className="hidden md:flex space-x-8">
            <Link to="/" className="text-gray-700 hover:text-pink-600">Home</Link>
            <Link to="/services" className="text-gray-700 hover:text-pink-600">Services</Link>
            <Link to="/gallery" className="text-gray-700 hover:text-pink-600">Gallery</Link>
            <Link to="/about" className="text-gray-700 hover:text-pink-600">About</Link>
            <Link to="/contact" className="text-gray-700 hover:text-pink-600">Contact</Link>
          </div>

          <button className="md:hidden" onClick={toggleMenu}>
            {isOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1">
              <Link to="/" className="block px-3 py-2 text-gray-700 hover:text-pink-600">Home</Link>
              <Link to="/services" className="block px-3 py-2 text-gray-700 hover:text-pink-600">Services</Link>
              <Link to="/gallery" className="block px-3 py-2 text-gray-700 hover:text-pink-600">Gallery</Link>
              <Link to="/about" className="block px-3 py-2 text-gray-700 hover:text-pink-600">About</Link>
              <Link to="/contact" className="block px-3 py-2 text-gray-700 hover:text-pink-600">Contact</Link>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
