import React, { useState, useEffect } from "react";
import axios from "axios";

const StaffAttendanceCard = ({ apiPath, title, description, color }) => {
  const [attendance, setAttendance] = useState([]);
  const [newAttendance, setNewAttendance] = useState({ name: "", date: "", status: "Present" });
  const [view, setView] = useState("default"); // "add", "mark", "fetch"

  const fetchAttendance = async () => {
    try {
      const response = await axios.get(`${apiPath}`);
      setAttendance(response.data);
    } catch (error) {
      console.error("Error fetching attendance records:", error);
    }
  };

  const addStaff = async () => {
    try {
      await axios.post(`${apiPath}`, newAttendance);
      setNewAttendance({ name: "", date: "", status: "Present" });
      alert("Staff added successfully!");
    } catch (error) {
      console.error("Error adding staff:", error);
    }
  };

  const markAttendance = async () => {
    try {
      await axios.post(`${apiPath}/mark`, newAttendance);
      fetchAttendance();
      setNewAttendance({ name: "", date: "", status: "Present" });
    } catch (error) {
      console.error("Error marking attendance:", error);
    }
  };

  const deleteAttendance = async (id) => {
    try {
      await axios.delete(`${apiPath}/${id}`);
      fetchAttendance();
    } catch (error) {
      console.error("Error deleting attendance record:", error);
    }
  };

  useEffect(() => {
    if (view === "fetch") fetchAttendance();
  }, [view]);

  return (
    
    <div className={`card border-${color} mb-4 shadow`}>
       <div style={{ marginTop: '70px' }}></div>
      <div className={`card-header bg-${color} text-white`}>{title}</div>
      <div className="card-body">
        <p>{description}</p>
        <div className="mb-3 d-flex justify-content-between">
          <button className="btn btn-primary" onClick={() => setView("add")}>
            Add Staff
          </button>
          <button className="btn btn-secondary" onClick={() => setView("mark")}>
            Mark Attendance
          </button>
          <button className="btn btn-success" onClick={() => setView("fetch")}>
            Fetch Data
          </button>
        </div>

        {view === "add" && (
          <div>
            <h5>Add Staff</h5>
            <input
              type="text"
              placeholder="Name"
              className="form-control mb-2"
              value={newAttendance.name}
              onChange={(e) => setNewAttendance({ ...newAttendance, name: e.target.value })}
            />
            <button className="btn btn-success" onClick={addStaff}>
              Add Staff
            </button>
          </div>
        )}

        {view === "mark" && (
          <div>
            <h5>Mark Attendance</h5>
            <input
              type="text"
              placeholder="Name"
              className="form-control mb-2"
              value={newAttendance.name}
              onChange={(e) => setNewAttendance({ ...newAttendance, name: e.target.value })}
            />
            <input
              type="date"
              className="form-control mb-2"
              value={newAttendance.date}
              onChange={(e) => setNewAttendance({ ...newAttendance, date: e.target.value })}
            />
            <select
              className="form-control mb-2"
              value={newAttendance.status}
              onChange={(e) => setNewAttendance({ ...newAttendance, status: e.target.value })}
            >
              <option value="Present">Present</option>
              <option value="Absent">Absent</option>
            </select>
            <button className="btn btn-warning" onClick={markAttendance}>
              Mark Attendance
            </button>
          </div>
        )}

        {view === "fetch" && (
          <div>
            <h5>Attendance Records</h5>
            <table className="table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Date</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {attendance.map((record) => (
                  <tr key={record.id}>
                    <td>{record.name}</td>
                    <td>{record.date}</td>
                    <td>{record.status}</td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => deleteAttendance(record.id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default StaffAttendanceCard;
