import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./styles/BookAppointment.css";

function BookAppointment() {
  const location = useLocation();
  const selectedServicesFromCart = location.state?.selectedServices || [];

  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState("");
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [customerName, setCustomerName,] = useState("");

  useEffect(() => {
    const fetchServices = async () => {
      const response = await axios.get("http://localhost:5000/api/services");
      setServices(response.data);
    };

    fetchServices();
  }, []);

  // Automatically select the first service from cart
  useEffect(() => {
    if (selectedServicesFromCart.length > 0) {
      setSelectedService(selectedServicesFromCart[0]._id); // Assuming services have `_id`
    }
  }, [selectedServicesFromCart]);

  const handleAppointmentBooking = async (e) => {
    e.preventDefault();
    if (!customerName || !contactNumber || !selectedService || !appointmentDate || !appointmentTime || contactNumber.length !== 10) {
      alert("Please fill all fields correctly. Contact number must be 10 digits.");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/appointments/add", {
        customerName,
        contactNumber,
        service: selectedService,
        appointmentDate,
        appointmentTime,
      });
      alert("Success! We'll call you back later to confirm the appointment.");
    } catch (error) {
      console.error("Error booking appointment:", error.response ? error.response.data : error);
    }
  };

  return (
    <div className="booking-container">
      <div style={{ marginTop: "90px" }}></div>
      <div className="banner">
        <div className="banner-overlay">
          <h1>Book Your Beauty Session</h1>
          <p>Your Perfect Look Awaits</p>
        </div>
      </div>

      <div className="container form-wrapper">
        <div className="booking-card">
          <h2 className="form-title">Appointment Details</h2>
          <form onSubmit={handleAppointmentBooking}>
            <div className="row g-3">
              <div className="col-md-6">
                <div className="form-group">
                  <label>Full Name</label>
                  <input type="text" className="form-control" value={customerName} onChange={(e) => setCustomerName(e.target.value)} required />
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-group">
                  <label>Contact Number</label>
                  <input
                    type="text"
                    className="form-control"
                    value={contactNumber}
                    onChange={(e) => {
                      const value = e.target.value;
                      if (/^\d{0,10}$/.test(value)) setContactNumber(value);
                    }}
                    required
                  />
                </div>
              </div>
            </div>

            <div className="form-group">
              <label>Select Service</label>
              <select className="form-select" value={selectedService} onChange={(e) => setSelectedService(e.target.value)} required>
                <option value="">-- Select Service --</option>
                {services.map((service) => (
                  <option key={service._id} value={service._id}>{service.name}</option>
                ))}
              </select>
            </div>

            <div className="row g-3">
              <div className="col-md-6">
                <div className="form-group">
                  <label>Date</label>
                  <input
                    type="date"
                    className="form-control"
                    value={appointmentDate}
                    min={new Date().toISOString().split("T")[0]}
                    onChange={(e) => setAppointmentDate(e.target.value)}
                    required
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-group">
                  <label>Time</label>
                  <input type="time" className="form-control" value={appointmentTime} onChange={(e) => setAppointmentTime(e.target.value)} required />
                </div>
              </div>
            </div>

            <button type="submit" className="btn submit-btn">Confirm Booking</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default BookAppointment;
