import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServicesPage = () => {
  const [services, setServices] = useState([]);
  const [categories, setCategories] = useState(['Hair', 'Skin', 'Nails', 'Makeup', 'Facial']); // Add categories here
  const [selectedCategory, setSelectedCategory] = useState('All'); // Default to "All"
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchServices(selectedCategory);
  }, [selectedCategory]);

  const fetchServices = async (category) => {
    try {
      const response = await axios.get('http://localhost:5000/api/services', {
        params: { category: category !== 'All' ? category : undefined },
      });
      setServices(response.data);
    } catch (err) {
      console.error('Error fetching services:', err);
      setError('Failed to load services. Please try again later.');
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Our Services</h1>
      {error && <p className="text-red-500 mb-4">{error}</p>}

      {/* Category Filter Buttons */}
      <div className="flex space-x-4 mb-6">
        {['All', ...categories].map((category) => (
          <button
            key={category}
            className={`px-4 py-2 rounded-lg ${
              selectedCategory === category
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-800'
            } hover:bg-blue-500 hover:text-white`}
            onClick={() => handleCategoryClick(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Services Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {services.length > 0 ? (
          services.map((service) => (
            <div
              key={service._id}
              className="bg-white shadow-md rounded-lg p-4 cursor-pointer hover:shadow-lg"
            >
              <h2 className="text-xl font-semibold mb-2">{service.name}</h2>
              <p className="text-gray-600">{service.description}</p>
              <p className="text-gray-700 font-bold">Price: ${service.price}</p>
              <p className="text-gray-600">Estimated Time: {service.time} mins</p>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No services available for this category. Please check back later.</p>
        )}
      </div>
    </div>
  );
};

export default ServicesPage;
