import React, { useState } from 'react';
import { Menu, X } from 'react-feather'; // Install react-feather for icons
import { Link } from 'react-router-dom'; // For routing

function Navbar() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setIsMenuOpen(false); // Close menu after clicking
    }
  };

  return (
    <div className="min-h-screen bg-violet-50">
      {/* Navbar */}
      <nav className="fixed w-full bg-white/95 backdrop-blur-sm shadow-sm z-50">
        <div className="max-w-6xl mx-auto px-4">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center gap-2">
              {/* Logo */}
              <span className="w-6 h-6 text-violet-500 font-bold">üíá‚Äç‚ôÄÔ∏è</span>
              <span className="font-bold text-violet-900">Bella Beauty</span>
            </div>

            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center gap-8">
              <button
                onClick={() => scrollToSection('services')}
                className="text-gray-600 hover:text-violet-500 transition-colors"
              >
                Services
              </button>
              <button
                onClick={() => scrollToSection('pricing')}
                className="text-gray-600 hover:text-violet-500 transition-colors"
              >
                Pricing
              </button>
              <button
                onClick={() => scrollToSection('gallery')}
                className="text-gray-600 hover:text-violet-500 transition-colors"
              >
                Gallery
              </button>
              <button
                onClick={() => scrollToSection('testimonials')}
                className="text-gray-600 hover:text-violet-500 transition-colors"
              >
                Testimonials
              </button>
              <button
                onClick={() => scrollToSection('contact')}
                className="text-gray-600 hover:text-violet-500 transition-colors"
              >
                Contact
              </button>
              <button
                onClick={() => scrollToSection('booking')}
                className="bg-violet-500 hover:bg-violet-600 text-white px-4 py-2 rounded-full text-sm transition-colors"
              >
                Book Now
              </button>
            </div>

            {/* Mobile Menu Button */}
            <button
              className="md:hidden text-gray-600"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
            >
              {isMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="md:hidden bg-white border-t border-gray-100">
            <div className="px-4 py-2 space-y-1">
              <button
                onClick={() => scrollToSection('services')}
                className="block w-full text-left px-3 py-2 text-gray-600 hover:bg-violet-50 rounded-md"
              >
                Services
              </button>
              <button
                onClick={() => scrollToSection('pricing')}
                className="block w-full text-left px-3 py-2 text-gray-600 hover:bg-violet-50 rounded-md"
              >
                Pricing
              </button>
              <button
                onClick={() => scrollToSection('gallery')}
                className="block w-full text-left px-3 py-2 text-gray-600 hover:bg-violet-50 rounded-md"
              >
                Gallery
              </button>
              <button
                onClick={() => scrollToSection('testimonials')}
                className="block w-full text-left px-3 py-2 text-gray-600 hover:bg-violet-50 rounded-md"
              >
                Testimonials
              </button>
              <button
                onClick={() => scrollToSection('contact')}
                className="block w-full text-left px-3 py-2 text-gray-600 hover:bg-violet-50 rounded-md"
              >
                Contact
              </button>
              <button
                onClick={() => scrollToSection('booking')}
                className="block w-full text-left px-3 py-2 text-violet-500 hover:bg-violet-50 rounded-md font-semibold"
              >
                Book Now
              </button>
            </div>
          </div>
        )}
      </nav>
    </div>
  );
}

export default Navbar;
