import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../pages/styles/Services.css';
import { FiClock, FiPlus } from 'react-icons/fi';
import { FaRupeeSign } from "react-icons/fa";
import Footer from "../components/Footer";

const categories = {
  Hair: ['Hair Spa', 'Hair Cut', 'Hair Wash', 'Hair Styling', 'Hair Coloring', 'Hair Oiling'],
  Packages: ['Basic', 'Premium', 'Luxury'],
  Makeup: ['Bridal Makeup', 'Casual Makeup', 'HD Makeup', 'Party Makeup'],
  Facial: ['Gold Facial', 'Diamond Facial', 'Fruit Facial', 'Anti-Aging Facial', 'Hydrating Facial', 'Pigmentation Facial', 'Acne Facial', 'Brightening Facial'],
  Skin: ['Skin Whitening', 'Acne Treatment', 'Glow Treatment', 'Pigmentation Treatment', 'Anti-Aging Treatment', 'Skin Tightening', 'Skin Rejuvenation', 'Skin Brightening', 'Skin Polishing', 'Skin Detoxification', 'Skin Hydration', 'Skin Nourishment'],
};

const ServicesPage = () => {
  const [services, setServices] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Hair');
  const [selectedSubcategory, setSelectedSubcategory] = useState('All');
  const [cart, setCart] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchServices(selectedCategory);
  }, [selectedCategory]);

  const fetchServices = async (category) => {
    try {
      const response = await axios.get('http://localhost:5000/api/services', {
        params: { category },
      });
      setServices(response.data);
    } catch (err) {
      console.error('Error fetching services:', err);
      setError('Failed to load services. Please try again later.');
    }
  };

  const filterBySubcategory = (service) => {
    return selectedSubcategory === 'All' || service.subcategory === selectedSubcategory;
  };

  const addToCart = (service) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find(item => item._id === service._id);
      if (existingItem) {
        return prevCart.map(item =>
          item._id === service._id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...service, quantity: 1 }];
    });
  };
  const updateQuantity = (serviceId, newQuantity) => {
    setCart((prevCart) =>
      prevCart.map(item =>
        item._id === serviceId ? { ...item, quantity: newQuantity } : item
      ).filter(item => item.quantity > 0)
    );
  };


  const removeFromCart = (serviceId) => {
    setCart((prevCart) => prevCart.filter(item => item._id !== serviceId));
  };


  const calculateTotal = () => {
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    const discount = total * 0.2; // 20% discount
    const finalPrice = total - discount;

    return { total, discount, finalPrice };
  };



  return (
    <div className="services-page">
      <div style={{ marginTop: '90px' }}></div>
      <div className="container-fluid">
        <div className="row">
          {/* Left Sidebar - Categories */}
          <div className="col-md-3 col-lg-2 p-4 border-end">
            <h4 className="mb-4 fw-bold">Categories</h4>
            <div className="d-flex flex-column gap-2">
              {Object.keys(categories).map((category) => (
                <button
                  key={category}
                  className={`btn btn-lg text-start rounded-pill ${selectedCategory === category ? 'btn-primary' : 'btn-outline-primary'}`}
                  onClick={() => {
                    setSelectedCategory(category);
                    setSelectedSubcategory('All');
                  }}
                >
                  {category}
                </button>
              ))}
            </div>
            {/* Subcategories */}
            {categories[selectedCategory] && (
              <div className="mt-4">
                <h5 className="fw-bold">Filter by</h5>
                <div className="d-flex flex-column gap-2">
                  <button
                    className={`btn btn-sm text-start ${selectedSubcategory === 'All' ? 'btn-dark' : 'btn-outline-dark'}`}
                    onClick={() => setSelectedSubcategory('All')}
                  >
                    All
                  </button>
                  {categories[selectedCategory].map((sub) => (
                    <button
                      key={sub}
                      className={`btn btn-sm text-start ${selectedSubcategory === sub ? 'btn-dark' : 'btn-outline-dark'}`}
                      onClick={() => setSelectedSubcategory(sub)}
                    >
                      {sub}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>
          {/* Middle Section - Services List */}
          <div className="col-md-6 col-lg-8 p-4 border-end">
            <h2 className="mb-4 fw-bold">{selectedCategory} Services</h2>
            <div className="services-list">
              {services.filter(filterBySubcategory).map((service) => (
                <div key={service._id} className="card mb-3 shadow-sm">
                  <div className="card-body">
                    <div className="row align-items-center">
                      <div className="col-md-3">
                        <img
                          src={`http://localhost:5000${service.image}`}
                          className="img-fluid rounded"
                          alt={service.name}
                        />
                      </div>
                      <div className="col-md-6">
                        <h5 className="fw-bold">{service.name}</h5>
                        <ul>
                          {service.description.split('. ').map((desc, index) => (
                            <li key={index}>{desc}</li>
                          ))}
                        </ul>
                        <div className="d-flex gap-3">
                          <span className="text-primary">
                            <FaRupeeSign /> {service.price}
                          </span>
                          <span className="text-muted">
                            <FiClock /> {service.time} mins
                          </span>
                        </div>
                      </div>
                      <div className="col-md-3 text-end">
                        <button
                          className="btn btn-primary"
                          onClick={() => addToCart(service)}
                        >
                          <FiPlus /> Add
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
          {/* Right Sidebar - Cart */}
          <div className="col-md-3 col-lg-2 p-4">
            <h4 className="mb-4 fw-bold">Your Cart</h4>
            {cart.length === 0 ? (
              <p className="text-muted">No items in cart</p>
            ) : (
              <>
                <div className="cart-items mb-4">
                  {cart.map((item) => (
                    <div className="d-flex justify-content-between align-items-center mb-2 p-2 border rounded">
                      <div>
                        <h6 className="mb-0">{item.name}</h6>
                        <small className="text-muted">
                          <FaRupeeSign /> {item.price} × {item.quantity}
                        </small>
                      </div>
                      <div className="d-flex align-items-center">
                        <button
                          className="btn btn-sm btn-outline-secondary me-2"
                          onClick={() => updateQuantity(item._id, item.quantity - 1)}
                          disabled={item.quantity <= 1}
                        >
                          -
                        </button>
                        <span className="fw-bold">{item.quantity}</span>
                        <button
                          className="btn btn-sm btn-outline-secondary ms-2"
                          onClick={() => updateQuantity(item._id, item.quantity + 1)}
                        >
                          +
                        </button>
                        <button
                          className="btn btn-sm btn-outline-danger ms-3"
                          onClick={() => removeFromCart(item._id)}
                        >
                          ×
                        </button>
                      </div>
                    </div>

                  ))}
                </div>
                <div className="total-price border-top pt-3">
                  <h6 className="d-flex justify-content-between">
                    <span>Total Amount:</span>
                    <span><FaRupeeSign /> {calculateTotal().total.toFixed(2)}</span>
                  </h6>
                  <h6 className="d-flex justify-content-between text-danger">
                    <span>Discount (20%):</span>
                    <span>- <FaRupeeSign /> {calculateTotal().discount.toFixed(2)}</span>
                  </h6>
                  <h5 className="d-flex justify-content-between fw-bold border-top pt-2">
                    <span>Final Price:</span>
                    <span><FaRupeeSign /> {calculateTotal().finalPrice.toFixed(2)}</span>
                  </h5>
                  <button
                    className="btn btn-primary w-100 mt-3"
                    onClick={() => navigate('/book-appointment')}
                  >
                    Continue to Book
                  </button>
                </div>

              </>
            )}
          </div>

        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ServicesPage;
