import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [appointments, setAppointments] = useState([]);
  const [showServices, setShowServices] = useState(false);
  const [showStaffAttendance, setShowStaffAttendance] = useState(false);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/appointments/today");
      setAppointments(response.data);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  const markAppointmentAsDone = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/api/appointments/${id}`, { status: "completed" });
      setAppointments(appointments.map((appointment) =>
        appointment._id === id ? { ...appointment, status: "completed" } : appointment
      ));
    } catch (error) {
      console.error("Error marking appointment as done:", error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Admin Dashboard</h1>
      <div className="row">
        <div className="col-md-12">
          <div className="card">
            <div className="card-header bg-primary text-white">
              <h4>Today's Appointments</h4>
            </div>
            <div className="card-body">
              {appointments.length > 0 ? (
                <ul className="list-group">
                  {appointments.map((appointment) => (
                    <li key={appointment._id} className="list-group-item">
                      <strong>{appointment.customerName}</strong><br />
                      Time: {appointment.appointmentTime}<br />
                      Contact: {appointment.contactNumber}<br />
                      Service: {appointment.service?.name || "N/A"}<br />
                      Status: {appointment.status || "pending"}
                      <button
                        className="btn btn-sm btn-success mt-2"
                        onClick={() => markAppointmentAsDone(appointment._id)}
                        disabled={appointment.status === "completed"}
                      >
                        {appointment.status === "completed" ? "Done" : "Mark as Done"}
                      </button>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No appointments for today.</p>
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-md-6">
          <button
            className="btn btn-secondary btn-block"
            onClick={() => setShowServices(!showServices)}
          >
            Manage Services
          </button>
          {showServices && (
            <div className="card mt-3">
              <div className="card-header bg-success text-white">
                <h4>Manage Services</h4>
              </div>
              <div className="card-body">
                {/* Add your ServiceForm component or service management logic here */}
                <p>Service management form goes here.</p>
              </div>
            </div>
          )}
        </div>

        <div className="col-md-6">
          <button
            className="btn btn-secondary btn-block"
            onClick={() => setShowStaffAttendance(!showStaffAttendance)}
          >
            Manage Staff Attendance
          </button>
          {showStaffAttendance && (
            <div className="card mt-3">
              <div className="card-header bg-secondary text-white">
                <h4>Manage Staff Attendance</h4>
              </div>
              <div className="card-body">
                {/* Add your staff attendance management logic here */}
                <p>Staff attendance management form goes here.</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
