import React, { useState, useEffect } from "react";
import axios from "axios";
import ServiceForm from "../components/ServiceForm";

const AdminDashboard = () => {
  const [appointments, setAppointments] = useState([]);
  const [services, setServices] = useState([]);
  const [staffAttendance, setStaffAttendance] = useState([]);

  useEffect(() => {
    fetchAppointments();
    fetchServices();
    fetchStaffAttendance();
  }, []);

  // Fetch today's appointments
  const fetchAppointments = async () => {
    try {
      const response = await axios.get("/api/appointments/today");
      setAppointments(response.data);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  // Fetch all available services
  const fetchServices = async () => {
    try {
      const response = await axios.get("/api/admin/services");
      setServices(response.data);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  // Fetch staff attendance status
  const fetchStaffAttendance = async () => {
    try {
      const response = await axios.get("/api/staff/attendance");
      setStaffAttendance(response.data);
    } catch (error) {
      console.error("Error fetching staff attendance:", error);
    }
  };

  // Delete a service
  const deleteService = async (id) => {
    try {
      await axios.delete(`/api/admin/services/${id}`);
      setServices(services.filter((service) => service._id !== id));
    } catch (error) {
      console.error("Error deleting service:", error);
    }
  };

  // Update staff attendance
  const toggleAttendance = async (staffId, currentStatus) => {
    try {
      await axios.put(`/api/staff/attendance/${staffId}`, {
        status: !currentStatus,
      });
      fetchStaffAttendance(); // Refresh staff attendance after update
    } catch (error) {
      console.error("Error updating attendance:", error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Admin Dashboard</h1>
      <div className="row">
        {/* Today's Appointments */}
        <div className="col-md-6">
          <div className="card">
            <div className="card-header bg-primary text-white">
              <h4>Today's Appointments</h4>
            </div>
            <div className="card-body">
              {appointments.length > 0 ? (
                <ul className="list-group">
                  {appointments.map((appointment) => (
                    <li key={appointment._id} className="list-group-item">
                      <strong>{appointment.customerName}</strong>
                      <br />
                      Service: {appointment.service.name}
                      <br />
                      Date: {new Date(appointment.appointmentDate).toLocaleDateString()}
                      <br />
                      Time: {appointment.appointmentTime}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No appointments for today.</p>
              )}
            </div>
          </div>
        </div>

        {/* Staff Attendance Management */}
        <div className="col-md-6">
          <div className="card">
            <div className="card-header bg-secondary text-white">
              <h4>Manage Staff Attendance</h4>
            </div>
            <div className="card-body">
              {staffAttendance.length > 0 ? (
                <ul className="list-group">
                  {staffAttendance.map((staff) => (
                    <li key={staff._id} className="list-group-item">
                      {staff.name} -{" "}
                      <span
                        className={`badge ${
                          staff.status ? "badge-success" : "badge-danger"
                        }`}
                      >
                        {staff.status ? "Present" : "Absent"}
                      </span>
                      <button
                        onClick={() => toggleAttendance(staff._id, staff.status)}
                        className="btn btn-sm btn-warning float-right"
                      >
                        Toggle Status
                      </button>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No staff attendance recorded yet.</p>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Manage Services */}
      <div className="row mt-4">
        <div className="col-md-12">
          <div className="card">
            <div className="card-header bg-success text-white">
              <h4>Manage Services</h4>
            </div>
            <div className="card-body">
              {/* Add Service Form Component */}
              <ServiceForm setServices={setServices} />
              <hr />
              <h5>Existing Services</h5>
              <ul className="list-group">
                {services.map((service) => (
                  <li key={service._id} className="list-group-item d-flex justify-content-between align-items-center">
                    <div>
                      <strong>{service.name}</strong>
                      <br />
                      {service.description}
                    </div>
                    <button
                      onClick={() => deleteService(service._id)}
                      className="btn btn-sm btn-danger"
                    >
                      Delete
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
