import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookAppointment() {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');
  const [appointmentTime, setAppointmentTime] = useState('');
  const [userName, setUserName] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [userId, setUserId] = useState(''); // Fetch this from logged-in user data
  const [confirmation, setConfirmation] = useState(false); // State to control confirmation pop-up

  useEffect(() => {
    const fetchServices = async () => {
      const response = await axios.get('http://localhost:5000/api/services');
      setServices(response.data);
    };

    fetchServices();
  }, []);

  const handleAppointmentBooking = async (e) => {
    e.preventDefault();

    // Prepare booking data
    const appointmentData = {
      name: userName,
      contact: contactNumber,
      date: appointmentDate,
      time: appointmentTime,
      serviceId: selectedService,
      userId, // Use user ID from the logged-in session
    };

    try {
      const response = await axios.post('http://localhost:5000/api/appointments', appointmentData);
      setConfirmation(true); // Show confirmation popup
      alert('We will call you later to confirm your appointment!');
    } catch (error) {
      console.error('Error booking appointment:', error.response.data);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Book Appointment</h2>
      <form onSubmit={handleAppointmentBooking}>
        {/* User Details Form */}
        <div className="mb-3">
          <label>Your Name</label>
          <input
            type="text"
            className="form-control"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label>Contact Number</label>
          <input
            type="text"
            className="form-control"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label>Select Service</label>
          <select
            className="form-control"
            value={selectedService}
            onChange={(e) => setSelectedService(e.target.value)}
            required
          >
            <option value="">-- Select Service --</option>
            {services.map((service) => (
              <option key={service._id} value={service._id}>
                {service.name}
              </option>
            ))}
          </select>
        </div>

        <div className="mb-3">
          <label>Appointment Date</label>
          <input
            type="date"
            className="form-control"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label>Appointment Time</label>
          <input
            type="time"
            className="form-control"
            value={appointmentTime}
            onChange={(e) => setAppointmentTime(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary">
          Book Appointment
        </button>
      </form>

      {/* Confirmation Pop-up */}
      {confirmation && (
        <div className="mt-4 alert alert-success" role="alert">
          We will call you later to confirm your appointment!
        </div>
      )}
    </div>
  );
}

export default BookAppointment;
