import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
// import './styles/BookAppointment.css';

const BookingPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedServices, setSelectedServices] = useState([]);
  const [userDetails, setUserDetails] = useState({ 
    name: '', 
    phone: '', 
    date: '', 
    time: '' 
  });

  useEffect(() => {
    if (location.state?.selectedServices) {
      setSelectedServices(location.state.selectedServices);
    }
  }, [location.state]);

  const handleInputChange = (e) => {
    setUserDetails({ ...userDetails, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const { name, phone, date, time } = userDetails;
    if (!name || !phone || !date || !time) {
      alert('Please fill in all fields');
      return false;
    }

    if (!/^\d{10}$/.test(phone)) {
      alert('Please enter a valid 10-digit phone number');
      return false;
    }

    const selectedDate = new Date(date);
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    if (selectedDate < today) {
      alert('Please select a date today or in the future');
      return false;
    }

    const [hours, minutes] = time.split(':').map(Number);
    if (hours < 10 || hours >= 20) {
      alert('Appointments available between 10:00 AM - 8:00 PM');
      return false;
    }

    if (selectedDate.toDateString() === today.toDateString()) {
      const now = new Date();
      const selectedTime = new Date();
      selectedTime.setHours(hours, minutes);
      
      if (selectedTime <= now) {
        alert('Please select a future time for today');
        return false;
      }
    }

    return true;
  };

  const handleBooking = async () => {
    if (!validateForm() || selectedServices.length === 0) return;

    try {
      const serviceIds = selectedServices.map(service => service._id);
      
      const response = await axios.post('http://localhost:5000/api/appointments/add', {
        customerName: userDetails.name,
        contactNumber: userDetails.phone,
        service: serviceIds,
        appointmentDate: userDetails.date,
        appointmentTime: userDetails.time,
      });

      if (response.status === 201) {
        alert("Appointment booked successfully!");
        navigate("/");
      }
    } catch (error) {
      console.error("Booking error:", error);
      alert("Failed to book appointment");
    }
  };

  return (
    <div className="booking-page container">
      <h2 className="page-title">Book Your Appointment</h2>
      
      <div className="selected-services card">
        <h4>Selected Services</h4>
        {selectedServices.length > 0 ? (
          <ul className="services-list">
            {selectedServices.map((service, index) => (
              <li key={index}>
                {service.name} - â‚¹{service.price}
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-muted">No services selected</p>
        )}
      </div>

      <div className="booking-form card">
        <input
          type="text"
          name="name"
          placeholder="Your Name"
          className="form-control"
          onChange={handleInputChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Phone Number"
          className="form-control"
          pattern="[0-9]{10}"
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="date"
          className="form-control"
          min={new Date().toISOString().split('T')[0]}
          onChange={handleInputChange}
          required
        />
        <input
          type="time"
          name="time"
          className="form-control"
          min="10:00"
          max="19:30"
          step="1800"
          onChange={handleInputChange}
          required
        />
        <button 
          className="btn btn-primary"
          onClick={handleBooking}
        >
          Confirm Booking
        </button>
      </div>
    </div>
  );
};

export default BookingPage;
