import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServiceForm = () => {
  // Hardcoded categories
  const categories = [
    { id: '1', name: 'Hair' },
    { id: '2', name: 'Skin' },
    { id: '3', name: 'Makeup' },
    { id: '4', name: 'Nails' },
  ];

  const [services, setServices] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: '',
    estimatedTime: '',
    price: '',
  });

  // Fetch services when a category is selected
  useEffect(() => {
    if (selectedCategory) {
      axios
        .get(`/api/admin/services/${selectedCategory}`)
        .then((res) => setServices(res.data))
        .catch((err) => console.error('Error fetching services:', err));
    }
  }, [selectedCategory]);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('/api/admin/services', formData)
      .then(() => {
        alert('Service added successfully!');
        setFormData({
          name: '',
          description: '',
          category: '',
          estimatedTime: '',
          price: '',
        });
        if (selectedCategory) {
          // Refresh services for the selected category
          axios
            .get(`/api/admin/services/${selectedCategory}`)
            .then((res) => setServices(res.data));
        }
      })
      .catch((err) => console.error('Error adding service:', err));
  };

  return (
    <div className="container mt-4">
      <h2>Manage Services</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Service Name</label>
          <input
            type="text"
            className="form-control"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Description</label>
          <textarea
            className="form-control"
            value={formData.description}
            onChange={(e) =>
              setFormData({ ...formData, description: e.target.value })
            }
            required
          ></textarea>
        </div>
        <div className="mb-3">
          <label className="form-label">Category</label>
          <select
            className="form-select"
            value={formData.category}
            onChange={(e) =>
              setFormData({ ...formData, category: e.target.value })
            }
            required
          >
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category.id} value={category.name}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <div className="mb-3">
          <label className="form-label">Estimated Time (in minutes)</label>
          <input
            type="number"
            className="form-control"
            value={formData.estimatedTime}
            onChange={(e) =>
              setFormData({ ...formData, estimatedTime: e.target.value })
            }
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Price (in $)</label>
          <input
            type="number"
            className="form-control"
            value={formData.price}
            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Add Service
        </button>
      </form>

      <hr />

      <h3>Services</h3>
      <div className="mb-3">
        <label className="form-label">Filter by Category</label>
        <select
          className="form-select"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="">Select a category</option>
          {categories.map((category) => (
            <option key={category.id} value={category.name}>
              {category.name}
            </option>
          ))}
        </select>
      </div>

      <ul>
        {services.map((service) => (
          <li key={service._id}>
            <strong>{service.name}</strong> - {service.description} (Time: {service.estimatedTime} min, Price: ${service.price})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ServiceForm;
