import React from "react";
import DashboardCard from "../components/DashboardCard";

const AdminDashboard = () => {
  const sections = [
    {
      title: "Today's Appointments",
      description: "View, add, delete, or fetch appointments for a specific date.",
      color: "primary",
      apiPath: "http://localhost:5000/api/appointments",
    },
    {
      title: "Manage Staff Attendance",
      description: "View, add, delete, or fetch staff attendance records.",
      color: "secondary",
      apiPath: "http://localhost:5000/api/staff/attendance",
    },
    {
      title: "Manage Services",
      description: "Add, update, or delete services offered.",
      color: "success",
      apiPath: "http://localhost:5000/api/services",
    },
  ];

  const renderDashboardCards = () =>
    sections.map((section, index) => (
      <div key={index} className="col-md-4">
        <DashboardCard
          title={section.title}
          description={section.description}
          color={section.color}
          apiPath={section.apiPath}
        />
      </div>
    ));

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Admin Dashboard</h1>
      <div className="row">{renderDashboardCards()}</div>
    </div>
  );
};

export default AdminDashboard;
