import React from "react";
import "./styles/WhatsAppButton.css"; // Import CSS for styling

const WhatsAppButton = () => {
  const phoneNumber = "+918447600553"; // Replace with your WhatsApp number
  const message = encodeURIComponent("Hello! I need some assistance."); // Default message

  return (
    <a
      href={`https://wa.me/${phoneNumber}?text=${message}`}
      target="_blank"
      rel="noopener noreferrer"
      className="whatsapp-button"
    >
      <img
        src="https://upload.wikimedia.org/wikipedia/commons/6/6b/WhatsApp.svg"
        alt="WhatsApp"
      />
    </a>
  );
};

export default WhatsAppButton;
