import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServiceForm = () => {
  const [services, setServices] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: '',
    time: '',
    price: '',
  });

  // Fetch services when the component mounts
  const fetchServices = async () => {
    try {
      const response = await axios.get('/api/services');
      setServices(response.data); // Store the fetched services in state
    } catch (error) {
      console.error('Error fetching services:', error);
    }
  };

  useEffect(() => {
    fetchServices(); // Fetch services on mount
  }, []);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Log the form data before sending it
    console.log(formData);

    axios
      .post('http://localhost:5000/api/services/add', formData)
      .then((response) => {
        alert('Service added successfully');
        setFormData({
          name: '',
          description: '',
          category: '',
          time: '',
          price: '',
        });
        fetchServices(); // Refresh the services list after adding
      })
      .catch((error) => {
        console.error('Error adding service:', error);
        alert('Failed to add service');
      });
  };

  // Filter services based on selected category
  const filteredServices = services.filter(
    (service) =>
      !selectedCategory || service.category === selectedCategory
  );

  return (
    <div className="container mt-4">
      <h2>Manage Services</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Service Name</label>
          <input
            type="text"
            className="form-control"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Description</label>
          <textarea
            className="form-control"
            value={formData.description}
            onChange={(e) =>
              setFormData({ ...formData, description: e.target.value })
            }
            required
          ></textarea>
        </div>
        <div className="mb-3">
          <label className="form-label">Category</label>
          <select
            className="form-select"
            value={formData.category}
            onChange={(e) =>
              setFormData({ ...formData, category: e.target.value })
            }
            required
          >
            <option value="">Select a category</option>
            <option value="Hair">Hair</option>
            <option value="Nails">Nails</option>
            <option value="Makeup">Makeup</option>
            <option value="Facial">Facial</option>
            <option value="Waxing">Waxing</option>
          </select>
        </div>
        <div className="mb-3">
          <label className="form-label">Time (in minutes)</label>
          <input
            type="number"
            className="form-control"
            value={formData.time}
            onChange={(e) =>
              setFormData({ ...formData, time: e.target.value })
            }
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Price (in $)</label>
          <input
            type="number"
            className="form-control"
            value={formData.price}
            onChange={(e) => setFormData({ ...formData, price: e.target.value })}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Add Service
        </button>
      </form>

      <hr />

      <h3>Services</h3>
      <div className="mb-3">
        <label className="form-label">Filter by Category</label>
        <select
          className="form-select"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="">Select a category</option>
          <option value="Hair">Hair</option>
          <option value="Nails">Nails</option>
          <option value="Makeup">Makeup</option>
          <option value="Facial">Facial</option>
          <option value="Waxing">Waxing</option>
        </select>
      </div>

      <ul>
        {filteredServices.length > 0 ? (
          filteredServices.map((service) => (
            <li key={service._id}>
              <strong>{service.name}</strong> - {service.description} (Time: {service.time} min, Price: ${service.price})
            </li>
          ))
        ) : (
          <li>No services available for this category.</li>
        )}
      </ul>
    </div>
  );
};

export default ServiceForm;
