import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
// import '../pages/styles/Booking.css';

const BookingPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [selectedServices, setSelectedServices] = useState([]);
  const [userDetails, setUserDetails] = useState({ name: '', phone: '', date: '', time: '' });

  // Load selected services from location state
  useEffect(() => {
    if (location.state && Array.isArray(location.state.selectedServices)) {
      setSelectedServices(location.state.selectedServices);
    } else {
      setSelectedServices([]); // Default to empty array
    }
  }, [location.state]);

  // Handle input field changes
  const handleInputChange = (e) => {
    setUserDetails({ ...userDetails, [e.target.name]: e.target.value });
  };

  // Handle appointment booking
  const handleBooking = async () => {
    if (!userDetails.name || !userDetails.phone || !userDetails.date || !userDetails.time) {
      alert('Please fill in all details.');
      return;
    }
  
    if (selectedServices.length === 0) {
      alert('No services selected.');
      return;
    }
  
    try {
      const serviceIds = selectedServices.map(service => service._id); // Extract IDs
  
      const response = await axios.post('http://localhost:5000/api/appointments/add', {
        customerName: userDetails.name,
        contactNumber: userDetails.phone,
        service: serviceIds, // Send IDs as an array
        appointmentDate: userDetails.date,
        appointmentTime: userDetails.time,
      });
  
      if (response.status === 201) {
        alert("Appointment booked successfully!");
        navigate("/");
      } else {
        alert("Failed to book appointment");
      }
    } catch (error) {
      console.error("Booking error:", error);
      alert("Failed to book appointment");
    }
  };
  

  return (
    <div className="booking-page container mt-5">
      <h2 className="fw-bold">Book Your Appointment</h2>
      
      <div className="selected-services mb-4">
        <h4>Selected Services</h4>
        {selectedServices.length > 0 ? (
          <ul>
            {selectedServices.map((service, index) => (
              <li key={index}>{service.name} - â‚¹{service.price}</li>
            ))}
          </ul>
        ) : (
          <p>No services selected.</p>
        )}
      </div>

      <div className="booking-form">
        <input type="text" name="name" placeholder="Your Name" className="form-control mb-3" onChange={handleInputChange} />
        <input type="text" name="phone" placeholder="Phone Number" className="form-control mb-3" onChange={handleInputChange} />
        <input type="date" name="date" className="form-control mb-3" onChange={handleInputChange} />
        <input type="time" name="time" className="form-control mb-3" onChange={handleInputChange} />
        <button className="btn btn-primary w-100" onClick={handleBooking}>Confirm Booking</button>
      </div>
    </div>
  );
};

export default BookingPage;
